cmd({
    pattern: "whatsappbetainfo",
    alias: ["wabi", "whatsapp"],
    desc: "Get news from NewsFirst.",
    category: "news",
    use: '.whatsappbetainfo',
    allowOnly: true,
  filename: __filename
}, async (conn, mek, m, { from, reply }) => {
    try {
        await m.react("🥏");
        const url = 'https://api.fsh.plus/html?url=https://wabetainfo.com';
        const { data } = await axios.get(url);
        const $ = cheerio.load(data);
        const firstArticle = $('.card-wrapper').first();
        const title = firstArticle.find('.entry-title a').text().trim();
        const link = firstArticle.find('.entry-title a').attr('href');
        const date = firstArticle.find('.entry-date').text().trim();
        const excerpt = firstArticle.find('.entry-excerpt').text().trim();
        const articleUrl = `https://api.fsh.plus/html?url=${link}`
        const articleResponse = await axios.get(articleUrl);
        const articleHtml = articleResponse.data;
        const article$ = cheerio.load(articleHtml);
        const paragraphs = article$('.kenta-article-content p').map((i, el) => article$(el).text().trim()).get();
        const description = paragraphs.join('\n\n')
        const image = article$('.image-container img').attr('src')
    
        const desc = `乂  *W A B E T A - I N F O*

 ◦  *Title :* ${title}
 ◦  *Date :* ${date}
 ◦  *Link :* ${link}
 ◦  *Info :* ${description}`;

        await conn.sendMessage(from, { image: { url: `https://api.codetabs.com/v1/proxy/?quest=${image}` }, caption: desc }, { quoted: mek });
        await m.react("✅");
        
    } catch (e) {
        console.log(e);
        reply(`${e}`);
    }
});

cmd({
    pattern: "gagana",
    alias: ["gagananews", "gag"],
    desc: "Get news from NewsFirst.",
    category: "news",
    use: '.gagana',
    allowOnly: true,
  filename: __filename
}, async (conn, mek, m, { from, reply }) => {
    try {
        await m.react("🧂");
        const response = await axios.get('https://gagana.lk/');
        const $ = cheerio.load(response.data);
        const firstNewsItem = $('.lsb-sec-items .a-item').first();
        const link = firstNewsItem.find('h4 a').attr('href');
        const image = firstNewsItem.find('img').attr('data-src');
        const resp = await axios.get(link);
        const $a = cheerio.load(resp.data);
        const title = $a('.ds-news-title h1').text().trim();
        const descr = $a('.ds-content p').map((i, el) => $a(el).text().trim()).get().join(' ');
        const date = $a('.ds-news-ts .timestamp').attr('title');
    
        const desc = `乂  *G A G A N A - N E W S*

 ◦  *Title :* ${title}
 ◦  *Date :* ${date}
 ◦  *Link :* ${link}
 ◦  *News :* ${descr}
 
`;

        await conn.sendMessage(from, { image: { url: image }, caption: desc }, { quoted: mek });
        await m.react("✅");
        
    } catch (e) {
        console.log(e);
        reply(`${e}`);
    }
});


cmd({
    pattern: "news",
    desc: "Get the latest news headlines.",
    category: "news",
    react: "📰",
    use: '.news',
    allowOnly: true,
  filename: __filename
},
async (conn, mek, m, { from, reply }) => {
    try {
        const apiKey="0f2c43ab11324578a7b1709651736382";
        const response = await axios.get(`https://newsapi.org/v2/top-headlines?country=us&apiKey=${apiKey}`);
        const articles = response.data.articles;

        if (!articles.length) return reply("No news articles found.");

        // Send each article as a separate message with image and title
        for (let i = 0; i < Math.min(articles.length, 5); i++) {
            const article = articles[i];
            let message = `
📰 *${article.title}*
⚠️ _${article.description}_
🔗 _${article.url}_

*© CLICKER-MD* 
> BY KelumXz,DanuXz   
            `;

            console.log('Article URL:', article.urlToImage); // Log image URL for debugging

            if (article.urlToImage) {
                // Send image with caption
                await conn.sendMessage(from, { image: { url: article.urlToImage }, caption: message });
            } else {
                // Send text message if no image is available
                await conn.sendMessage(from, { text: message });
            }
        };
    } catch (e) {
        console.error("Error fetching news:", e);
        reply("Could not fetch news. Please try again later.");
    }
});

